mapper.select:
select format(sum(lprice*qnt),0) as fmtsum
from view_cart
where uid=#{uid};

mapper.update:
update cart
set qnt=#{qnt}
where cid=#{cid}

update cart
set qnt = qnt + #{qnt}
where pid=#{pid} and uid=#{uid}

mapper.select:
select count(*)
from cart
where pid=#{pid} and uid=#{uid}

        select *, (lprice*qnt) as sum, format(lprice*qnt, 0) as fmtsum
        from view_cart
        where uid=#{uid}
		order by regdate desc
		limit #{start}, #{size}

select count(*)
from cart
where uid=#{uid}
